import React, { useState } from 'react';
import axios from 'axios';

const RegistrationForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
    });

    const [errors, setErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');


    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setErrors({
            ...errors,
            [name]: undefined,
        });

        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Front-end validation
        const validationErrors = {};
        if (formData.password !== formData.confirmPassword) {
            validationErrors.confirmPassword = 'Passwords do not match';
        }
        // Add more validation checks if necessary

        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        // Call the function to send data to Laravel API
        sendDataToLaravel(formData);
    };


    const sendDataToLaravel = (data) => {
        axios
            .post('http://127.0.0.1:8000/api/send-data', {
                name: data.name,
                email: data.email,
                password: data.password,
                password_confirmation: data.confirmPassword, // Include password_confirmation field
            }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            })
            .then((response) => {
                console.log('Response from Laravel:', response.data);
                setSuccessMessage('User registered successfully');
                setTimeout(() => {
                    setSuccessMessage('User registered successfully');
                }, 1000);
            })
            .catch((error) => {
                console.error('Error:', error);
                if (error.response && error.response.data && error.response.data.errors) {
                    setErrors(error.response.data.errors);
                }
            });
    };


    return (
        <div>
            <h2>Registration Form</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="name">Username:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        required
                    />
                    {errors.name && <span className="error">{errors.name}</span>}
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                    />
                    {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div>
                    <label htmlFor="password">Password:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        required
                    />
                    {errors.password && <span className="error">{errors.password}</span>}
                </div>
                <div>
                    <label htmlFor="confirmPassword">Confirm Password:</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleInputChange}
                        required
                    />
                    {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
                </div>
                <button type="submit">Register</button>
                {successMessage && (
                    <div className="success-message">
                        {successMessage}
                    </div>
                )}
            </form>
        </div>
    );
};

export default RegistrationForm;
