import React, { useState, useEffect } from 'react';
import axios from 'axios';
function UserLists() {
    const [userData, setUserData] = useState([]);



    const sendDataToLaravel = () => {
        const requestData = {
            key: {
                'id': 'test',
                'name': 'Rehan'
            },
        };

        console.log('Request Data:', requestData); // Request data ko console par show karta hai

        axios.post('http://127.0.0.1:8000/api/send-data', JSON.stringify(requestData), {
            headers: {
                'Content-Type': 'application/json',
                // Laravel backend ke liye CSRF token bhi yahan include kiya ja sakta hai agar required hai
                // 'X-CSRF-TOKEN': csrfToken,
            },
        })
            .then(response => {
                // Laravel se aane wale response ko handle karta hai
                console.log('Response from Laravel:', response.data);
            })
            .catch(error => {
                // Request ke doran jo errors aati hain unko handle karta hai
                console.error('Error:', error);
            });
    };

// sendDataToLaravel function ko call karte hain
    sendDataToLaravel();



    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('http://127.0.0.1:8000/api/resource')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setUserData(data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []); // Empty dependency array ensures the effect runs once after the initial render

    // Define the function to send data to Laravel


    // Call sendDataToLaravel when userData changes (i.e., after data is fetched from /api/resource)
    useEffect(() => {

        sendDataToLaravel();
    }, [userData]); // Call this effect whenever userData changes

    return (

        <div>
            <h1>User List</h1>
            <ul>
                {userData && userData.map((user) => (
                    <li key={user.id}>
                        Name: {user.name}
                        Email: {user.email}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default UserLists;
