import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserLists() {
    const [userData, setUserData] = useState([]);

    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('http://127.0.0.1:8000/api/resource')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setUserData(data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []); // Empty dependency array ensures the effect runs once after the initial render

    // Define the function to send data to Laravel
    const sendDataToLaravel = () => {
        const generateRandomString = (length) => {
            const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
            let result = '';
            for (let i = 0; i < length; i++) {
                result += characters.charAt(Math.floor(Math.random() * characters.length));
            }
            return result;
        };

        const requestData = () => {
            const key = generateRandomString(10); // Generate a random string of length 10 as the key
            const data = {
                name: generateRandomString(8), // Generate a random name of length 8
                email: generateRandomString(6) + '@example.com', // Generate a random email-like string
                address: generateRandomString(12) + ' Street', // Generate a random address-like string
                // Add more properties and generate data as needed
            };

            return { key, data };
        };


        axios.post('http://127.0.0.1:8000/api/send-data', requestData, {
            headers: {
                'Content-Type': 'application/json',
                // Include CSRF token in request headers (if required by your Laravel backend)
                // 'X-CSRF-TOKEN': csrfToken,
            },
        })
            .then(response => {
                console.log('Response from Laravel:', response.data);
                // Handle the response from Laravel here
            })
            .catch(error => {
                console.error('Error:', error);
                if (error.response) {
                    // The request was made and the server responded with a status code
                    // that falls out of the range of 2xx
                    console.error('Response data:', error.response.data);
                    console.error('Response status:', error.response.status);
                    console.error('Response headers:', error.response.headers);
                } else if (error.request) {
                    // The request was made but no response was received
                    console.error('Request data:', error.request);
                } else {
                    // Something happened in setting up the request that triggered an Error
                    console.error('Error message:', error.message);
                }
            });

    };

    // Call sendDataToLaravel when userData changes (i.e., after data is fetched from /api/resource)
    useEffect(() => {

        sendDataToLaravel();
    }, [userData]); // Call this effect whenever userData changes

    return (

        <div>
            <h1>User List</h1>
            <ul>
                {userData && userData.map((user) => (
                    <li key={user.id}>
                        Name: {user.name}
                        Email: {user.email}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default UserLists;
