import React, { useState, useEffect } from 'react';
import axios from 'axios';
function UserLists() {
    const [userData, setUserData] = useState([]);

    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('http://127.0.0.1:8000/api/resource')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setUserData(data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []); // Empty dependency array ensures the effect runs once after the initial render

    // Define the function to send data to Laravel
    const sendDataToLaravel = async () => {


        axios.post('http://127.0.0.1:8000/api/send-data', {
            key: 'value', // Your data to be sent
        })
            .then(response => console.log(response.data))
            .catch(error => console.error('Error:', error));



    };

    // Call sendDataToLaravel when userData changes (i.e., after data is fetched from /api/resource)
    useEffect(() => {

        sendDataToLaravel();
    }, [userData]); // Call this effect whenever userData changes

    return (

        <div>
            <h1>User List</h1>
            <ul>
                {userData && userData.map((user) => (
                    <li key={user.id}>
                        Name: {user.name}
                        Email: {user.email}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default UserLists;
