import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

function Dashboard() {
    const navigate = useNavigate(); // Use useNavigate instead of useHistory

    const handleLogout = () => {
        // Clear the token from local storage (or perform your logout API request here)
        localStorage.removeItem('token');
        // Redirect to the login page
        navigate('/login'); // Use navigate function to redirect
    };

    const isAuthenticated = () => {
        // Check if the user is authenticated (token exists in local storage)
        return localStorage.getItem('token') !== null;
    };

    return (
        <Container>
            <>
                <Row className="row align-items-center d-flex pt-5 mt-5">
                    <Col lg={12} sm={12} className="form_col pt-5 mt-5">
                        {isAuthenticated() ? (
                            <>
                                <section className="OurBestPlatform">
                                    <p>You Are Logged In.</p>
                                </section>
                                <Button variant="danger" onClick={handleLogout}>
                                    Logout
                                </Button>
                            </>
                        ) : (
                            // Redirect to the login page if not authenticated
                            navigate('/login') // Use navigate function here as well
                        )}
                    </Col>
                </Row>
            </>
        </Container>
    );
}

export default Dashboard;
