import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';


const RegistrationForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
    });
    const [errors, setErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setErrors({
            ...errors,
            [name]: undefined,
        });

        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const validationErrors = {};
        if (formData.password !== formData.confirmPassword) {
            validationErrors.confirmPassword = 'Passwords do not match';
        }


        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        sendDataToLaravel(formData);
    };


    const sendDataToLaravel = (data) => {
        axios
            .post('http://127.0.0.1:8000/api/send-data', {
                name: data.name,
                email: data.email,
                password: data.password,
                password_confirmation: data.confirmPassword,
            }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            })
            .then((response) => {
                console.log('Response from Laravel:', response.data);
                setSuccessMessage('User registered successfully');

            })
            .catch((error) => {
                console.error('Error:', error);
                if (error.response && error.response.data && error.response.data.errors) {
                    setErrors(error.response.data.errors);
                }
            });
    };


    return (
    <section className="login">
        <Container fluid >
            <Row className="row align-items-center d-flex">
                <Col lg={5} sm={12} className="form_col">
                    <h2>Welcome To
                        Your Trading Desk!</h2>
                    <form action="" className="login_form">
                        <input
                            type="text"
                            id="name"
                            name="name"
                            className="form-control"
                            required
                            placeholder="User Name"
                        />
                        <input
                            type="email"
                            id="email"
                            name="email"
                            className="form-control"

                            onChange={handleInputChange}
                            required
                            placeholder="Email"
                        />
                        <div className="pass">
                            <input
                                type="password"
                                id="password"
                                name="password"
                                className="form-control"
                                onChange={handleInputChange}
                                required
                                placeholder="Password"
                            />
                            <input
                                type="password"
                                id="password"
                                name="confirm-password"
                                className="form-control"
                                onChange={handleInputChange}
                                required
                                placeholder="Confirm Password"
                            />
                        </div>
                        <input type="button" className="btn_form" value="Login"/>
                    </form>
                    <div className="signup_opt">
                        <a href="#">Already have an account</a>
                        <button>Sign in</button>
                    </div>
                </Col>
                <Col lg={7} sm={12}>
                    <img src="../images/login_img.png" alt=""/>
                </Col>
            </Row>
        </Container>
    </section>
    );
};

export default RegistrationForm;
