import React, { useState, useEffect } from 'react';

function UserLists() {
    const [userData, setUserData] = useState([]);

    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('http://127.0.0.1:8000/api/resource')
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setUserData(data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, []); // Empty dependency array ensures the effect runs once after the initial render

    // Define the function to send data to Laravel
    const sendDataToLaravel = async () => {
        try {
            const csrfToken = document.querySelector('meta[name="csrf-token"]').getAttribute('content');

            const response = await fetch('http://127.0.0.1:8000/api/send-data', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'X-CSRF-TOKEN': csrfToken, // Include CSRF token here
                },
                body: JSON.stringify({
                    key: 'value', // Your data to be sent
                }),
            });

            if (response.ok) {
                const data = await response.json();
                console.log(data); // Handle the response from Laravel
            } else {
                console.error('Error sending data to Laravel');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };


    // Call sendDataToLaravel when userData changes (i.e., after data is fetched from /api/resource)
    useEffect(() => {
        sendDataToLaravel();
    }, [userData]); // Call this effect whenever userData changes

    return (
        <div>
            <h1>User List</h1>
            <ul>
                {userData && userData.map((user) => (
                    <li key={user.id}>
                        Name: {user.name}
                        Email: {user.email}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default UserLists;
